  var offset,
      offsetDelta;
  if (this.customScrollMode) {
    offset = this._getOffsetStep_Custom();
  } else {
    offsetDelta = this._getOffsetStep_Buttons();
    offset = offsetDelta;
  }
  this.scrollToolbarBy(offset);
